/// Routines
function GetCompCount() { 
    var k = 0;
    for (var i = 1; i <= app.project.numItems; i++) {
        if (app.project.item(i) instanceof CompItem) {
            k++;
        }
    }    
    return k;
}

function LoadConfiguration() {
    if (OS == "darwin") {
        var TempFile = new File ("~/Documents/AErender/Script.cfg");
    } else if (OS == "windows") {
        var TempFile = new File ("/c/ProgramData/AErender/Script.cfg");
    }

    if (TempFile != null) {
        TempFile.encoding = "UTF-8";
        TempFile.open("r", "Text", "Error reading configuration");
        var LauncherPath = TempFile.readln();
        TempFile.close();
    }
    return LauncherPath;
}

function SaveConfiguration() {
    var LauncherPath = SettingsForm.TopPanel.LauncherPath.text;
    if (OS == "darwin") {
        var TempFile = new File ("~/Documents/AErender/Script.cfg");
    } else if (OS == "windows") {
        var TempFile = new File ("/c/ProgramData/AErender/Script.cfg");
    }
    if (TempFile != null) {
        TempFile.encoding = "UTF-8";
        TempFile.open("w", "Text", "Error saving configuration");
        TempFile.writeln(LauncherPath);
        TempFile.close();
    }
}

function exec(command) {
    if (OS == "darwin") {
        var execFile = new File ("~/Documents/AErender/Script.command");
        if (execFile != null) {
            execFile.encoding = "UTF-8";
            execFile.open("w", "Text", "Error");
            execFile.writeln(command);
            execFile.close();
            system.callSystem("chmod +x " + execFile.fsName);
            execFile.execute();
        }
    }
    if (OS == "windows") {
        var execFile = new File ("/c/ProgramData/AErender/Script.bat");
        if (execFile != null) {
            execFile.encoding = "UTF-8";
            execFile.open("w", "Text", "Error");
            execFile.writeln("chcp 65001");
            execFile.writeln(command);
            execFile.close();
            execFile.execute();
        }
    }
}

function GetComposition(name) {
    var result = null;
    for (var i = 1; i <= app.project.numItems; i++) {
        if (app.project.items[i] instanceof CompItem && app.project.items[i].name == name) {
            result = app.project.items[i];
        }
    }
    return result;
}

function IsArrayItemContainsString(array, string) {
    var result = false;
    for (var i = 0; i < array.length; i++) {
        if (array[i].indexOf(string) != -1) {
            result = true;
            break;
        }
    }
    return result;
}

function GetAER() {
    var RootNode = <aer project={app.project.file.fsName} platform={OS}></aer>;
    var CompositionsNode = <compositions count={GetCompCount()}></compositions>;

    for (var i = 1; i <= app.project.numItems; i++) {
        if (app.project.items[i] instanceof CompItem) {
            var CompNode = <comp></comp>;
            var CompNameNode = <name>{app.project.items[i].name}</name>;
            var CompResolutionNode = <resolution>{app.project.items[i].width + "x" + app.project.items[i].height}</resolution>;
            var CompFramerateNode = <framerate>{app.project.items[i].frameRate}</framerate>;
            var CompRangeStartNode = <rangeStart>{(app.project.items[i].workAreaStart * app.project.items[i].frameRate)}</rangeStart>;
            var CompRangeEndNode = <rangeEnd>{((app.project.items[i].workAreaStart * app.project.items[i].frameRate) + (app.project.items[i].workAreaDuration * app.project.items[i].frameRate))}</rangeEnd>;
            
            CompNode.appendChild(CompNameNode);
            CompNode.appendChild(CompResolutionNode);
            CompNode.appendChild(CompFramerateNode);
            CompNode.appendChild(CompRangeStartNode);
            CompNode.appendChild(CompRangeEndNode);

            CompositionsNode.appendChild(CompNode);
        }
    }

    RootNode.appendChild(CompositionsNode);

    return RootNode.toXMLString();
}

function GetAERQ(index, renderSettings, outputmodule, outputfolder) {
    var RootNode = <aerq project={app.project.file.fsName}></aerq>;

    if (outputfolder == true) {
        var QueueItemNode = <queueItem outputFolder={thisRenderQueue.item(index).outputModule(1).getSettings(GetSettingsFormat.STRING_SETTABLE)["Output File Info"]["Base Path"]}></queueItem>;
    } else {
        var QueueItemNode = <queueItem outputFolder=""></queueItem>;
    }

    var OutputModuleNode = <outputModule use={outputmodule}></outputModule>;

    if (outputmodule == true) {
        var OMNameNode = <module>{thisRenderQueue.item(index).outputModule(1).name}</module>;
        var OMMaskNode = <mask>{thisRenderQueue.item(index).outputModule(1).getSettings(GetSettingsFormat.STRING_SETTABLE)["Output File Info"]["File Template"]}</mask>;
    } else {
        var OMNameNode = <module></module>;
        var OMMaskNode = <mask></mask>;
    }
    
    if (renderSettings === "Don't export Render Settings") {
        var RenderSettingsNode  = <renderSettings></renderSettings>;
    } else {
        var RenderSettingsNode  = <renderSettings>{renderSettings}</renderSettings>;
    }
    

    var CompositionNode     = <composition></composition>;

    var CompNameNode        = <name>{thisRenderQueue.item(index).comp.name}</name>;
    var CompFramerateNode   = <framerate>{GetComposition(thisRenderQueue.item(index).comp.name).frameRate}</framerate>;
    var CompRangeStartNode  = <rangeStart>{GetComposition(thisRenderQueue.item(index).comp.name).workAreaStart * GetComposition(thisRenderQueue.item(index).comp.name).frameRate}</rangeStart>;
    var CompRangeEndNode    = <rangeEnd>{(GetComposition(thisRenderQueue.item(index).comp.name).workAreaStart * GetComposition(thisRenderQueue.item(index).comp.name).frameRate) + (GetComposition(thisRenderQueue.item(index).comp.name).workAreaDuration * GetComposition(thisRenderQueue.item(index).comp.name).frameRate)}</rangeEnd>;

    OutputModuleNode.appendChild(OMNameNode);
    OutputModuleNode.appendChild(OMMaskNode);

    CompositionNode.appendChild(CompNameNode);
    CompositionNode.appendChild(CompFramerateNode);
    CompositionNode.appendChild(CompRangeStartNode);
    CompositionNode.appendChild(CompRangeEndNode);

    QueueItemNode.appendChild(OutputModuleNode);
    QueueItemNode.appendChild(RenderSettingsNode);
    QueueItemNode.appendChild(CompositionNode);

    RootNode.appendChild(QueueItemNode);

    return RootNode.toXMLString();
}

